import React, { useState } from 'react';
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { Paper, Grid, Typography } from '@mui/material';
import axios from 'axios';

import { CIE10_CODES } from '../data/CIE10Codes';
import { MEDICAMENTOS } from '../data/Medicamentos';
import { ANTECEDENTES } from '../data/Antecedentes';

const NuevaConsulta = () => {
  const [datosPaciente, setDatosPaciente] = useState({
    nombre: '',
    fechaNacimiento: '',
    telefono: '',
    correo: '',
  });

  const [signosVitales, setSignosVitales] = useState({
    peso: '',
    talla: '',
    frecuenciaCardiaca: '',
    presionArterial: '',
    temperatura: '',
    glucosa: '',
  });

  const [antecedentes, setAntecedentes] = useState([]);
  const [otrosAntecedentes, setOtrosAntecedentes] = useState('');
  const [motivoConsulta, setMotivoConsulta] = useState('');
  const [exploracionFisica, setExploracionFisica] = useState('');
  const [diagnostico, setDiagnostico] = useState('');
  const [tratamiento, setTratamiento] = useState([]);

  const handleGuardar = async () => {
    const datos = {
      nombre: datosPaciente.nombre,
      fecha_nacimiento: datosPaciente.fechaNacimiento,
      telefono: datosPaciente.telefono,
      correo: datosPaciente.correo,

      peso: signosVitales.peso,
      talla: signosVitales.talla,
      frecuencia_cardiaca: signosVitales.frecuenciaCardiaca,
      presion_arterial: signosVitales.presionArterial,
      temperatura: signosVitales.temperatura,
      glucosa: signosVitales.glucosa,

      antecedentes: antecedentes.join(', '),
      otros_antecedentes: otrosAntecedentes,

      motivo_consulta: motivoConsulta,
      exploracion_fisica: exploracionFisica,
      diagnostico,
      tratamiento: tratamiento.join(', ')
    };

    try {
      const response = await axios.post('http://localhost:8000/api/consultas/', datos);
      console.log('‚úÖ Consulta guardada:', response.data);

      // ‚úÖ Abrir receta en PDF si se gener√≥ correctamente
      if (response.data.receta_pdf_url) {
        window.open(response.data.receta_pdf_url, '_blank');
      } else {
        alert('Consulta guardada, pero no se pudo abrir la receta.');
      }
    } catch (error) {
      console.error('üö® Error al guardar la consulta:', error);
      console.log('üß© Detalles del error:', error.response?.data);
      alert('Hubo un problema al guardar la consulta');
    }
  };









  return (
    <Paper
      elevation={4}
      sx={{
        p: 4,
        maxWidth: 900,
        margin: '2rem auto',
        backgroundColor: '#f9f9f9',
        borderRadius: 3,
      }}
    >
      <Typography variant="h4" gutterBottom align="center" fontWeight="bold">
        Nueva Consulta
      </Typography>

      {/* Datos del Paciente */}
      <section style={{ marginBottom: '2rem' }}>
        <Typography variant="h6" gutterBottom>
          Datos del Paciente
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField label="Nombre Completo" value={datosPaciente.nombre} onChange={(e) => setDatosPaciente({ ...datosPaciente, nombre: e.target.value })} fullWidth />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField label="Fecha de Nacimiento" type="date" InputLabelProps={{ shrink: true }} value={datosPaciente.fechaNacimiento} onChange={(e) => setDatosPaciente({ ...datosPaciente, fechaNacimiento: e.target.value })} fullWidth />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField label="Tel√©fono Celular" value={datosPaciente.telefono} onChange={(e) => setDatosPaciente({ ...datosPaciente, telefono: e.target.value })} fullWidth />
          </Grid>
          <Grid item xs={12}>
            <TextField label="Correo Electr√≥nico (Opcional)" value={datosPaciente.correo} onChange={(e) => setDatosPaciente({ ...datosPaciente, correo: e.target.value })} fullWidth />
          </Grid>
        </Grid>
      </section>

      {/* Signos Vitales */}
      <section style={{ marginBottom: '2rem' }}>
        <Typography variant="h6" gutterBottom>
          Signos Vitales
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={6} sm={4}><TextField label="Peso (kg)" value={signosVitales.peso} onChange={(e) => setSignosVitales({ ...signosVitales, peso: e.target.value })} fullWidth /></Grid>
          <Grid item xs={6} sm={4}><TextField label="Talla (cm)" value={signosVitales.talla} onChange={(e) => setSignosVitales({ ...signosVitales, talla: e.target.value })} fullWidth /></Grid>
          <Grid item xs={6} sm={4}><TextField label="Frecuencia Card√≠aca (lpm)" value={signosVitales.frecuenciaCardiaca} onChange={(e) => setSignosVitales({ ...signosVitales, frecuenciaCardiaca: e.target.value })} fullWidth /></Grid>
          <Grid item xs={6} sm={4}><TextField label="Presi√≥n Arterial (mmHg)" value={signosVitales.presionArterial} onChange={(e) => setSignosVitales({ ...signosVitales, presionArterial: e.target.value })} fullWidth /></Grid>
          <Grid item xs={6} sm={4}><TextField label="Temperatura (¬∞C)" value={signosVitales.temperatura} onChange={(e) => setSignosVitales({ ...signosVitales, temperatura: e.target.value })} fullWidth /></Grid>
          <Grid item xs={6} sm={4}><TextField label="Glucosa (mg/dL)" value={signosVitales.glucosa} onChange={(e) => setSignosVitales({ ...signosVitales, glucosa: e.target.value })} fullWidth /></Grid>
        </Grid>
      </section>

      {/* Antecedentes */}
      <section style={{ marginBottom: '2rem' }}>
        <Typography variant="h6" gutterBottom>
