      {/* Exploraci√≥n F√≠sica */}
      <section style={{ marginBottom: '2rem' }}>
        <Typography variant="h6" gutterBottom>
          Exploraci√≥n F√≠sica
        </Typography>
        <TextField label="Exploraci√≥n F√≠sica" value={exploracionFisica} onChange={(e) => setExploracionFisica(e.target.value)} multiline rows={3} fullWidth />
      </section>

      {/* Diagn√≥stico */}
      <section style={{ marginBottom: '2rem' }}>
        <Typography variant="h6" gutterBottom>
          Diagn√≥stico (CIE-10)
        </Typography>
        <Autocomplete options={CIE10_CODES} getOptionLabel={(option) => option} value={diagnostico} onChange={(event, newValue) => setDiagnostico(newValue)} renderInput={(params) => <TextField {...params} label="Diagn√≥stico" />} fullWidth />
      </section>

      {/* Tratamiento */}
      <section style={{ marginBottom: '2rem' }}>
        <Typography variant="h6" gutterBottom>
          Tratamiento y Medicamentos
        </Typography>
        <Autocomplete multiple freeSolo options={MEDICAMENTOS} getOptionLabel={(option) => option} value={tratamiento} onChange={(event, newValue) => setTratamiento(newValue)} renderInput={(params) => <TextField {...params} label="Medicamentos" />} fullWidth />
      </section>

      {/* Bot√≥n Final */}
      <section style={{ marginTop: '2rem' }}>
      <Button variant="contained" color="primary" onClick={handleGuardar} fullWidth sx={{ py: 1.5 }}>
  Guardar y Generar Receta
</Button>

      </section>
    </Paper>
  );
};

export default NuevaConsulta;
